function o(){return Math.floor(Math.random()*6)+1}function n(){return new RSVP.Promise(function(o,n){var t=Math.floor(Math.random()*6)+1;if(t===6){o(t)}else{n(t)}})}function t(o){console.log("Tossed a "+o+", need to try again.");return n()}function e(o){console.log("Yay, managed to toss a "+o+".")}function a(o){console.log("Tossed a "+o+". Too bad, couldn't roll a six")}n().then(null,t).then(null,t).then(e,a);